import React from 'react';

import {withRouter} from 'react-router-dom';
import { connect } from "react-redux";
import { addUser} from "../redux/actions";

export class Create extends React.Component {

    constructor(props){
        super(props);
    }

    setName(event) {
        event.preventDefault();
        this.props.nameState(event.target[0].value);
    }

    takeInformation(event)
    {
        event.preventDefault();
        this.props.addUser({
            firstName : event.target[0].value,
            lastName : event.target[1].value ,
            birthDate: event.target[2].value ,
            bio : event.target[3].value,
            city: event.target[4].value
        });
    }


    render(){
        return(
            <div class="display-create createAccount">
                <div class="text-center">
                    <h3>La création d'un utilisateur</h3>
                </div>
                <div>
                <form onSubmit={ event => this.takeInformation(event) }>
                    <div class="row">
                        <div class="col create-form"></div>
                        <div class="col create-form">
                            <label > Votre Prénom :</label>
                        </div>
                        <div class="col-1 text-center create-form">
                            <input type="text"/>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col create-form"></div>
                        <div class="col create-form">
                            <label >Votre Nom : </label>
                        </div>
                        <div class="col-1 text-center create-form">
                            <input type="text"/>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col create-form"></div>
                        <div class="col create-form">
                            <label> Votre date de naissance :</label>
                        </div>
                        <div class="col-1 text-center create-form">
                            <input type="text"/>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col create-form"></div>
                        <div class="col create-form">
                            <label >Votre biographie : </label>
                        </div>
                        <div class="col-1 text-center create-form">
                            <input type="text"/>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col create-form"></div>
                        <div class="col create-form">
                            <label > Votre ville :</label>
                        </div>
                        <div class="col-1 text-center create-form">
                            <input type="text"/>
                        </div>
                    </div>
                    <div class="col-5 text-center create-form ">
                        <button class="validate-form">valider</button>
                    </div>                    
                </form>
                </div>
            </div>
        );
    }
}
const mapStateToProps = state => {
    return {
        scores: state.scores
    };
};

const mapDispatchToProps = dispatch => {
    return {
        addUser: user => {
            dispatch(addUser(user))
        },
    };
};

export default withRouter(connect(
    mapStateToProps,
    mapDispatchToProps
)(Create));
