{
    constructor() {
        super();
        const rand = Math.round(Math.random()*(100));
        this.state={
            randNb :rand,
            proposition : ''
    };

        console.log(this.state.randNb);
    }

    setProposition(content) {
        console.log('nb a trouver', content);
        this.setState({
            ...this.state,proposition:content
        })
        this.find();
    }

    find(){
        if (this.state.randNb<this.state.proposition){
            console.log("en dessous");
        }
        else
        {
            console.log("au dessus");
        }
    }

    render(){
        return (
            <div>
            <h2>nombre a trouver</h2>
            <p>vous devez trouver le nombre entre 1 et 100</p>
            <form onSubmit={ event => this.find(event) }>
    <label>
        proposition : <input type="text" />
            </label>
            <button onClick={event => this.setProposition(event)}>valider</button>
            <button >recommencer</button>

            </form>
            </div>
    );
    }
}
