        event.preventDefault();
        let nbGiven = parseInt(event.target[0].value);
        if (nbGiven === this.state.nbRand) {
            console.log("find: " + this.compteur);
            this.setState({...this.state, content : "vous avez trouvé aprés " + this.compteur + " essai"});
