            console.log("en dessous" + this.compteur);
            this.compteur ++;
            this.setState({...this.state, content : "vous avez fait " + this.compteur + " essai"});
        } else if (nbGiven < this.state.nbRand) {
            console.log("au dessus");
            this.compteur ++;
            this.setState({...this.state, content : "vous avez fait " + this.compteur + " essai"});
        }
    }

    generateRand(){
        let nb = Math.round(Math.random()*(101));
        console.log(nb);
        return nb;
    }

    restart(){
        console.log("perdu");
