import React from 'react';

export default class LeNombreATrouver extends React.Component
{
    constructor() {
        super();
        this.compteur = 0;
        this.nbRand = this.generateRand();
        this.state={
            randNb :this.nbRand,
            nbTry : 0
    };
        console.log(this.state.randNb);
    }

    number(event) {
        event.preventDefault();
        let nbGiven = parseInt(event.target[0].value);
        if (nbGiven == this.nbRand) {
            console.log("find");
        } else if (nbGiven < this.nbRand) {
            console.log("en dessous")
            this.compteur ++;
        } else if (nbGiven > this.nbRand) {
            console.log("au dessus")
            this.compteur++;
        }
    }

    generateRand(){
        return Math.round(Math.random()*(101));
    }

    restart(event){
        console.log("perdu");
        //save dans database
        this.nbRand = this.generateRand();
    }

    render(){
        return (
            <div>
            <h2>nombre a trouver</h2>
            <p>vous devez trouver le nombre entre 1 et 100</p>
            <form onSubmit={ event => this.number(event) }>
            <label>
        proposition : <input type="text" />
            </label>
            <button>valider</button>
            <button onClick={event => this.restart(event)}>recommencer</button>

            </form>
            </div>
    );
    }
}
